(101, 7, 'MB', 'Was anyone injured?', 'Accident', 28, 'Yes,Wobbler,injury|No,,', '', 0),
(102, 7, 'EX', '<b>Accident</b><br/><br/>A DUI with injury is a misdemeanor of the first degree&#44; which carries a possible jail sentence up to 1 year and a fine up to $1000. A DUI with serious bodily injury is a felony of the third degree&#44; which carries a possible jail sentence up to 5 years and a fine up to $5000. Serious bodily injury include substantial risk of death&#44; serious personal disfigurement&#44; or loss or impairment of bodily functions.', 'Wobbler', 29, 'Continue,,,', '', 0),
(103, 7, 'MB', 'Was there a passenger under the age of 14 in the car?', 'Minors', 15, 'Yes,High BAC,child passenger|No,Priors,', '', 0),
(105, 4, 'EX', '<b>Possible Penalty</b><br/><br/>Jail - 10 days to 1 year<br/>Fines - $1800 to $2800<br/>License Suspension - 2 years', 'Second', 5, 'Continue,,', '', 0),
(106, 4, 'EX', '<b>Possible Penalty</b><br/><br/>Jail - 120 days to 1 year<br/>Fines - $1800 to $18000<br/>License Suspension - 3 years', 'Third', 6, 'Continue,,', '', 0),
(107, 4, 'EX', 'If you have had 3 or more separate DUI convictions in the past 10 years&#44; this offense will most likely be charged as a felony.<br/><br/><b>Possible Penalties</b><br/><br/>Jail - up to 3 years<br/>License Suspension - 4 years or more', 'More', 7, 'Continue,,,', '', 0),
(108, 6, 'EX', '<b>Possible Penalty</b></br><br/>Jail &ndash; 30 days to 1 year<br/>Fines &ndash; up to $4000<br/>License Suspension &ndash; 180 days to 2 year', 'Second', 5, 'Continue,,,', '', 0),
(109, 6, 'EX', '<b>Possible Penalty</b></br><br/>If you have had 2 or more separate DWI convictions in the past 10 years&#44; this offense will most likely be charged as a felony.<br/>Jail - 2 years<br/>Fines - up to $10000<br/>License Suspension - 180 days to 2 years', 'Third', 6, 'Continue,,,', '', 0),
(110, 7, 'MC', 'How many times have you been convicted of drunk driving in the past?', 'Priors', 20, 'This is my first,First2,|One time,Second2,|Two times,Third2,|Three or more times,More,', '', 0),
(111, 7, 'EX', '<b>Possible Penalty</b><br/><br/>Jail - up to 9 months<br/>Fines - $1000 to $2000<br/>License Suspension - 180 days to 1 year', 'First1', 17, 'Continue,,,', '', 0),
(112, 7, 'EX', '<b>Possible Penalty</b><br/><br/>Jail - up to 1 year&#44; unless you&#39;ve had another DUI in the past 5 years&#44; then 10 days mandatory<br/>Fines - $2000 to $4000<br/>License Suspension - 1 year&#44; unless this DUI is within 5 year of a previous DUI conviction&#44; then 5 years', 'Second1', 18, 'Continue,,,', '', 0),
(113, 7, 'EX', '<b>Possible Penalty</b><br/><br/>Jail - up to 1 year (mandatory 30 days if this is your third conviction within 10 years)<br/>Fines - $4000 minimum<br/>License Suspension - 2 years&#44; unless this is your third conviction with 10 years&#44; then 10 years', 'Third1', 19, 'Continue,,,', '', 0),
(114, 7, 'EX', '<b>Possible Penalty</b><br/><br/>Jail - up to 5 years<br/> Fines - $2000 minimum<br/>License Suspension - for life', 'More', 24, 'Continue,,,', '', 0),
(115, 7, 'EX', '<b>Possible Penalty</b><br/><br/>Jail - up to 6 months<br/>Fines - $500 to $1000<br/>License Suspension - 180 days to 1 year', 'First2', 21, 'Continue,,,', '', 0),
(116, 7, 'EX', '<b>Possible Penalty</b><br/><br/>Jail - up to 9 months&#44; unless you&#39;ve had another DUI in the past 5 years&#44; then 10 days mandatory<br/>Fines - $1000 to $2000<br/>License Suspension - 1 year&#44; unless you&#39;ve had another DUI in the past 5 years&#44; then 5 years', 'Second2', 22, 'Continue,,,', '', 0),
(117, 7, 'EX', '<b>Possible Penalty</b><br/><br/>Jail - up to 1 year (mandatory 30 days if this is your third conviction within 10 years)<br/>Fines - $2000 to $5000<br/>License Suspension - 2 years&#44; unless this is your third conviction with 10 years&#44; then 10 years', 'Third2', 23, 'Continue,,,', '', 0),
(118, 7, 'EX', 'Have you ever refused the chemical test in the past?', 'Second Refusal', 13, 'Yes,Second Refusal1,|No,,', '', 0),
(119, 7, 'EX', '<b>Refusal</b><br/><br/>If you refuse the chemical test a second time&#44; you can be charged with a separate misdemeanor of the first degree for the first offense. A misdemeanor of the first degree carries up to 1 year of jail time and a fine up to $1000. ', 'Second Refusal1', 14, 'Continue,Minors,,', '', 0),
